  <div class="personal-info-container">
    <div class="avatar-container">
      <div class="avatar-content">
        <a-image :width="200" class="avatar-image"
          :src="imgSrc"
          fallback="data:image/png;base64," />
      </div>
      <div class="edit-img">
        <a-button type="link" @click="editAvatar">
          <template #icon>
            <EditOutlined />
          </template>
          <span v-if="!isEdit">修改头像</span>
          <span v-if="isEdit">取消修改</span>
        </a-button>
      </div>
    </div>
    <div class="info-details">
      <div class="info-item">
        <label>用户名:</label>
        <span>{{ userInfoStore.nickname || userInfoStore.username }}</span>
      </div>
      <div class="info-item">
        <label>电子邮件:</label>
        <span>{{ userInfoStore.email }}</span>
      </div>
      <div class="info-item">
        <label>电话:</label>
        <span>{{ userInfoStore.phone || '***********' }}</span>
      </div>
      <div class="info-item">
        <label>地址:</label>
        <span>{{ userInfoStore.address || '地球' }}</span>
      </div>
      <div class="info-item">
        <label>生日:</label>
        <span>{{ moment(userInfoStore.birthday).format('l') || '长生不老' }}</span>
      </div>
    </div>
    <a-button type="dashed" danger @click="logout">
      <template #icon>
        <LogoutOutlined />
      </template>
      退出登录
    </a-button>
  </div>
  <div v-if="isEdit" class="edit-avatar-img">
    <CropperImage></CropperImage>
  </div>
</template>
  
<script lang="ts" setup>
import { useUserInfoStore } from '@/stores/userInfo';
import moment from 'moment';
import { LogoutOutlined, EditOutlined } from '@ant-design/icons-vue';
import CropperImage from '@/untils/CropperImage.vue';
import router from '@/router';
import { bufferToBase64 } from '@/untils/bufferToBase64';

const userInfoStore = useUserInfoStore().userInfoData;
const isEdit = ref<boolean>(false);

const editAvatar = () => {
  isEdit.value = !isEdit.value
}
const logout = () => {
  localStorage.clear();
  router.push({ path: '/login', replace: true });
}

const imgSrc = bufferToBase64(userInfoStore.avatar as unknown as {data: ArrayBuffer})
</script>
  
